import React, { useState } from 'react';
import { Navbar, Button, Container } from 'react-bootstrap';
import AddAuctionModal from './AddAuctionModal';
import eauctionHeader from '../assets/eauction-header.png';
import '../auction.css';

export default function Home({ addr }) {
  const [showAddAuctionModal, setShowAddAuctionModal] = useState(false);

  return (
    <>
      <section>
        <div
          style={{
            backgroundImage: `url(${eauctionHeader})`,
            backgroundRepeat: 'no-repeat',
            backgroundPosition: 'center',
            backgroundSize: 'cover',
          }}>
          <div className='container' style={{ minHeight: '550px' }}>
            <div className='text-center justify-content-center align-self-center'>
              <h1 className='pt-5 pb-3'>{title}</h1>
              <h5>{subTitle}</h5>
            </div>
          </div>
        </div>
      </section>
      <AddAuctionModal
        show={showAddAuctionModal}
        handleClose={() => setShowAddAuctionModal(false)}
        assetOwner={addr}
      />
    </>
  );
}
