import React, { useState } from 'react';
import { Navbar, Button, Container, Row, Col } from 'react-bootstrap';
import AddAuctionModal from './AddAuctionModal';
import auction from '../assets/auction.png';
import '../auction.css';

export default function Home({ addr }) {
  const [showAddAuctionModal, setShowAddAuctionModal] = useState(false);

  return (
    <>
      <section>
        <div
          style={{
            backgroundColor: '#F7F7F7',
            backgroundRepeat: 'no-repeat',
            backgroundPosition: 'center',
            backgroundSize: 'cover',
            height: '400px',
          }}>
          <div className='container' style={{ minHeight: '550px' }}>
            <div className='text-center justify-content-center align-self-center'>
              <Row>
                <Col>
                  <img src={auction} style={{ maxHeight: '200px' }} />
                </Col>
                <Col>
                  <h2>Welcome To</h2>
                  <h1>E-Auction Decentralized Website</h1>
                  <Button
                    variant='primary'
                    size='lg'
                    onClick={() => setShowAddAuctionModal(true)}>
                    Create Auction
                  </Button>
                </Col>
              </Row>
            </div>
          </div>
        </div>
      </section>
      <AddAuctionModal
        show={showAddAuctionModal}
        handleClose={() => setShowAddAuctionModal(false)}
        assetOwner={addr}
      />
    </>
  );
}
